(function(){const GRID_CLASSES=['grid-2','grid-3','grid-4'];const GRID_SELECTOR=GRID_CLASSES.map(cls=>`.${cls}`).join(',');const isGridBlock=element=>element&&GRID_CLASSES.some(cls=>element.classList&&element.classList.contains(cls));const collected=new Set();const gridBlocks=document.querySelectorAll(GRID_SELECTOR);gridBlocks.forEach(block=>{if(collected.has(block))return;const cluster=[block];let sibling=block.nextElementSibling;while(isGridBlock(sibling)){cluster.push(sibling);collected.add(sibling);sibling=sibling.nextElementSibling;}cluster.forEach(node=>collected.add(node));wrapCluster(cluster);});function wrapCluster(cluster){if(!cluster.length)return;const container=document.createElement('div');const dominantSize=cluster.reduce((max,el)=>{const size=GRID_CLASSES.find(cls=>el.classList.contains(cls));if(!size)return max;const numeric=parseInt(size.split('-')[1],10);return Math.max(max,numeric);},1);const classList=['custom-grid-container'];if(dominantSize>=2){classList.push(`grid-${dominantSize}`);}if(cluster.some(el=>el.classList.contains('grid-sm-2'))){classList.push('grid-sm-2');}container.className=classList.join(' ');cluster[0].parentNode.insertBefore(container,cluster[0]);cluster.forEach(node=>container.appendChild(node));}document.querySelectorAll('.custom-grid-container .image-component>.frame').forEach(frame=>{const computedWidth=window.getComputedStyle(frame).width;const widthInRem=parseFloat(computedWidth)/parseFloat(getComputedStyle(document.documentElement).fontSize);if(widthInRem>20){frame.classList.add('constrain-width');}});})();/**Early Page Animation Trigger*Starts CSS animations on DOM ready instead of waiting for full window load.*Removes loader and triggers scroll/resize handlers immediately for faster visual feedback.*/(function(){'use strict';var initialized=false;function getBody(){return document.body;}function markReadyNow(){var body=getBody();if(!body)return;body.classList.remove('is-loading','with-loader');if(!body.classList.contains('is-ready')){body.classList.add('is-playing');setTimeout(function(){body.classList.remove('is-playing');body.classList.add('is-ready');},750);}}function hideLoaderIfPresent(){var loader=document.getElementById('loader');if(loader){loader.style.cssText='display:none;visibility:hidden;opacity:0';}}function kickScrollHandlers(){var pulses=0;var maxPulses=15;var timer=setInterval(function(){if(++pulses>=maxPulses){clearInterval(timer);return;}try{window.dispatchEvent(new Event('resize'));window.dispatchEvent(new Event('scroll'));}catch(e){/*ignore*/}},75);var rafPulses=0;(function rafTick(){try{window.dispatchEvent(new Event('resize'));window.dispatchEvent(new Event('scroll'));}catch(e){/*ignore*/}if(++rafPulses<5)requestAnimationFrame(rafTick);})();}function setupObservers(){var body=getBody();if(!body)return;var classObserver=new MutationObserver(function(mutations){mutations.forEach(function(m){if(m.attributeName==='class'&&body.classList.contains('with-loader')){body.classList.remove('with-loader');}});});var childObserver=new MutationObserver(hideLoaderIfPresent);classObserver.observe(body,{attributes:true,attributeFilter:['class']});childObserver.observe(body,{childList:true});setTimeout(function(){classObserver.disconnect();childObserver.disconnect();},5000);}function init(){if(initialized)return;initialized=true;try{markReadyNow();hideLoaderIfPresent();setupObservers();kickScrollHandlers();}catch(e){console.warn('early-animate-override failed:',e);}}if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',init,{once:true});}else{init();}})();