(function(){const parsePadding =(val)=>{if(!val)return null;return val.split(' ').map(v => !isNaN(v)? v + 'rem':v).join(' ');};const initCards =()=>{const cardContainers = document.querySelectorAll('.cards');cardContainers.forEach(container =>{if(container.dataset.cardsInitialized === 'true')return;container.dataset.cardsInitialized = 'true';const style = window.getComputedStyle(container);const backgroundColor = style.backgroundColor;const borderRadius = style.borderRadius;const dataPadding = parsePadding(container.dataset.padding);const dataPaddingMobile = parsePadding(container.dataset.paddingMobile);container.style.setProperty('background-color','transparent','important');const inner = container.querySelector('.inner');if(!inner)return;const columns = Array.from(inner.children);columns.forEach(column =>{if(column.querySelector('.card-item'))return;const cardItem = document.createElement('div');cardItem.classList.add('card-item');if(backgroundColor && backgroundColor !== 'rgba(0,0,0,0)' && backgroundColor !== 'transparent'){cardItem.style.backgroundColor = backgroundColor;}else{cardItem.style.backgroundColor = '#cccccc';}if(borderRadius && borderRadius !== '0px'){cardItem.style.setProperty('--card-border-radius',borderRadius);}if(dataPadding){cardItem.style.setProperty('--card-padding',dataPadding);}if(dataPaddingMobile){cardItem.style.setProperty('--card-padding-mobile',dataPaddingMobile);}while(column.firstChild){cardItem.appendChild(column.firstChild);}column.appendChild(cardItem);});});};if(document.readyState === 'loading'){document.addEventListener('DOMContentLoaded',initCards);}else{initCards();}window.addEventListener('load',initCards);})();