(function(){const CONTAINER_SELECTOR='.FAQContainer';const DIVIDER_SELECTOR='hr.divider-component';const HEADER_TAGS=new Set(['H1','H2','H3']);let answerIdCounter=0;const containers=document.querySelectorAll(CONTAINER_SELECTOR);if(!containers.length)return;containers.forEach(container=>{const dividers=Array.from(container.querySelectorAll(DIVIDER_SELECTOR)).filter(divider=>divider.closest(CONTAINER_SELECTOR)===container);if(!dividers.length)return;dividers.forEach(startDivider=>{const endDivider=findNextDivider(startDivider);const header=findHeaderBetween(startDivider,endDivider);if(!header||header.dataset.faqBound==='true'){return;}const answerWrapper=header.nextElementSibling&&header.nextElementSibling.classList.contains('faq-answer')?header.nextElementSibling:wrapAnswerContent(header,endDivider);if(!answerWrapper){return;}prepareToggle(header,answerWrapper);});});window.addEventListener('resize',()=>{document .querySelectorAll(`${CONTAINER_SELECTOR}.faq-answer.is-open`).forEach(answer=>adjustHeight(answer));});function findNextDivider(divider){let node=divider.nextElementSibling;while(node){if(isDivider(node)){return node;}node=node.nextElementSibling;}return null;}function findHeaderBetween(startDivider,endDivider){let node=startDivider.nextElementSibling;let firstParagraph=null;while(node&&node!==endDivider){if(isHeader(node)){return node;}if(!firstParagraph&&node.nodeType===1&&node.tagName==='P'){firstParagraph=node;}node=node.nextElementSibling;}if(!endDivider){while(node){if(isHeader(node)){return node;}if(!firstParagraph&&node.nodeType===1&&node.tagName==='P'){firstParagraph=node;}node=node.nextElementSibling;}}return firstParagraph;}function wrapAnswerContent(header,endDivider){let node=header.nextSibling;const wrapper=document.createElement('div');wrapper.className='faq-answer';let hasContent=false;while(node&&node!==endDivider){const next=node.nextSibling;wrapper.appendChild(node);hasContent=true;node=next;}if(!hasContent){return null;}if(endDivider&&endDivider.parentNode){header.parentNode.insertBefore(wrapper,endDivider);}else{header.parentNode.appendChild(wrapper);}return wrapper;}function prepareToggle(header,answer){header.classList.add('faq-question');header.dataset.faqBound='true';header.classList.add('is-closed');if(!header.hasAttribute('tabindex')){header.setAttribute('tabindex','0');}header.setAttribute('role','button');header.setAttribute('aria-expanded','false');if(!answer.id){answerIdCounter+=1;answer.id=`faq-answer-${answerIdCounter}`;}answer.setAttribute('aria-hidden','true');answer.classList.add('is-closed');header.setAttribute('aria-controls',answer.id);const toggle=()=>toggleAnswer(header,answer);header.addEventListener('click',toggle);header.addEventListener('keydown',event=>{if(event.key==='Enter'||event.key===' '){event.preventDefault();toggle();}});}function toggleAnswer(header,answer){const willOpen=!header.classList.contains('is-open');header.classList.toggle('is-open',willOpen);header.classList.toggle('is-closed',!willOpen);answer.classList.toggle('is-open',willOpen);answer.classList.toggle('is-closed',!willOpen);header.setAttribute('aria-expanded',String(willOpen));answer.setAttribute('aria-hidden',String(!willOpen));if(willOpen){adjustHeight(answer);}else{answer.style.maxHeight='0px';}}function adjustHeight(answer){answer.style.maxHeight=`${answer.scrollHeight}px`;}function isHeader(node){return node&&node.nodeType===1&&HEADER_TAGS.has(node.tagName);}function isDivider(node){return(node&&node.nodeType===1&&node.tagName==='HR'&&node.classList.contains('divider-component'));}})();/***Grid Cluster Script-Groups consecutive grid blocks into containers*Script:Finds consecutive grid elements,wraps them in custom-grid-container,*applies largest grid size to container,constrains wide images*Required classes:*-grid-2,grid-3,grid-4:Creates 2/3/4-column layouts*-grid-sm-2:Optional 2-column on small screens*/(function(){const GRID_CLASSES=['grid-2','grid-3','grid-4','grid-5','grid-6'];const GRID_SELECTOR=GRID_CLASSES.map(cls=>`.${cls}`).join(',');const isGridBlock=element=>element&&GRID_CLASSES.some(cls=>element.classList&&element.classList.contains(cls));const getGridSize=element=>{if(!element||!element.classList)return null;const sizeClass=GRID_CLASSES.find(cls=>element.classList.contains(cls));if(!sizeClass)return null;const numeric=parseInt(sizeClass.split('-')[1],10);return Number.isNaN(numeric)?null:numeric;};const collected=new Set();const gridBlocks=document.querySelectorAll(GRID_SELECTOR);gridBlocks.forEach(block=>{if(collected.has(block))return;const cluster=[block];const baseSize=getGridSize(block);let sibling=block.nextElementSibling;while(isGridBlock(sibling)){const siblingSize=getGridSize(sibling);if(baseSize!==null&&siblingSize!==baseSize){break;}cluster.push(sibling);collected.add(sibling);sibling=sibling.nextElementSibling;}collected.add(block);wrapCluster(cluster,baseSize);});function wrapCluster(cluster,gridSize){if(!cluster.length||!cluster[0].parentNode)return;const container=document.createElement('div');const classList=['custom-grid-container'];if(gridSize&&gridSize>=2){classList.push(`grid-${gridSize}`);}if(cluster.some(el=>el.classList.contains('grid-sm-2'))){classList.push('grid-sm-2');}container.className=classList.join(' ');cluster[0].parentNode.insertBefore(container,cluster[0]);cluster.forEach(node=>container.appendChild(node));}document.querySelectorAll('.custom-grid-container .image-component>.frame').forEach(frame=>{const computedWidth=window.getComputedStyle(frame).width;const widthInRem=parseFloat(computedWidth)/parseFloat(getComputedStyle(document.documentElement).fontSize);if(widthInRem>20){frame.classList.add('constrain-width');}});})();